import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom'
import bookDef from '../assets/data'

const EditPage = () => {
    let params = useParams();
    let bookId = params.id;
    console.log("EDIT:",params)

    // let bookId = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
    let [book, setBook] = useState(bookDef)

    book.id = bookId
    useEffect(() => {
        getBook()
    }, [bookId])

    let getBook = async () => {
        let response = await fetch(`http://localhost:8000/api/books/${bookId}`)
        let data = await response.json()
        console.log("data:",data)
        setBook(data)
    }

    let updateComment = async () => {
        await fetch(`http://localhost:8000/api/books/${bookId}`, {
            method: 'PUT',
            header: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(book)
        })
    }

    let handleSubmit = () => {
        updateComment()
    }

    return (
        <form className="editform">
            <label className="editLabel">id:
                <input type="text" value={book.id} />
            </label>
            <br></br>
            <label className="editLabel">isbn:
                <input type="text" value={book.isbn}  onChange={(e)=> {setBook({...book, 'isbn':e.target.value})}}/>
            </label>
            <br></br>
            <label className="editLabel">title:
                <input type="text" value={book.title}  onChange={(e)=> {setBook({...book, 'title':e.target.value})}}/>
            </label>
            <br></br>
            <label className="editLabel">author:
                {/* <input type="text" value={book.author.firstname} /> */}
                <input type="text" value={book.author.firstname}  onChange={(e)=> {setBook({...book, 'author.firstname':e.target.value})}}/>
                <input type="text" value={book.author.lastname}  onChange={(e)=> {setBook({...book, 'author.lastname':e.target.value})}}/>
            </label>

            <button onClick={handleSubmit} className="UpdateBtn">
                &#10002;
            </button>
        </form>
    )
}

export default EditPage
