import React from 'react'
import { NavLink, useParams } from 'react-router-dom'

const Header = () => {
    // let params = useParams();
    let bookId = this.href.substring(this.href.lastIndexOf('/') + 1);

    let deleteBook = async () => {
        // console.log("Deleting ID:",bookId)
        await fetch(`http://localhost:8000/api/books/${bookId}`, {
            method: 'DELETE',
            header: {
                'Content-Type': 'application/json',
            }
        })
    }

    let handleDel = () => {
        deleteBook()
    }

    return(
        <ul className='UL-List'>
            <NavLink to='/find' className="HeadItem" id="HeadItem-R">
            Find
            </NavLink>
            <NavLink to='/' onClick={handleDel} className="HeadItem" id="HeadItem-Del">
            Delete
            </NavLink>
            <NavLink to='/' className="HeadItem" id="HeadItem-L">
            Home
            </NavLink>
        </ul>
        
    )
}

export default Header;
