import React from 'react'
import { NavLink, useParams } from 'react-router-dom'

const Header = () => {
    let params = useParams();
    // let bookId = params.id;
    // let bookId = this.href.substring(this.href.lastIndexOf('/') + 1);
    // let linkLocation = window.location.href;

    let deleteBook = async () => {
        let bookId = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
        console.log("Deleting ID:",bookId)
        // console.log("Last Part:",linkLocation);
        await fetch(`http://localhost:8000/api/books/${bookId}`, {
            method: 'DELETE',
            header: {
                'Content-Type': 'application/json',
            }
        })
    }

    return(
        <ul className='UL-List'>
            <NavLink to='/find' className="HeadItem" id="HeadItem-R">
            Find
            </NavLink>
            <li onClick={deleteBook} className="HeadItem" id="HeadItem-Del">
            Delete
            </li>
            <NavLink to='/' className="HeadItem" id="HeadItem-L">
            Home
            </NavLink>
        </ul>
        
    )
}

export default Header;
