import React, {useEffect, useState} from 'react'
import { Link, useParams } from 'react-router-dom'
import bookDef from '../assets/data'

const BookPage = () => {
    let params = useParams();
    let bookId = params.id;
    // let book = books.find(book => book.id == bookId)
    let [book, setBook] = useState(bookDef)

    useEffect(() => {
        getBook()
    }, [bookId])

    let getBook = async () => {
        let response = await fetch(`http://localhost:8000/api/books/${bookId}`)
        let data = await response.json()
        console.log("data:",data)
        setBook(data)
    }

    let updateComment = async () => {
        await fetch(`http://localhost:8000/api/books/${bookId}`, {
            method: 'PUT',
            mode: 'cors',
            header: {
                'Content-Type': 'application/json',
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Headers": "Content-Type"
            },
            comment: book.comment
        })
    }

    let handleSubmit = () => {
        updateComment()
    }

    return (
        <div className="Box-Text">
            <button onClick={handleSubmit}>
                Update
            </button>
            <h3>{book.title} &nbsp;&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp; {book.id}</h3>
            <h7>{book.isbn}</h7>
            <h5>{book.author.firstname} {book.author.lastname}</h5>

            <textarea onChange={(e)=> {setBook({...book, 'comment':e.target.value})}} value={book?.comment}>
            
            </textarea>
            
        </div>
    )
}

export default BookPage
