import React from 'react'
import { NavLink, useParams } from 'react-router-dom'
import bookDef from '../assets/data'

const Header = () => {
    let bookId = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
    // console.log("Pre Edit ID:",bookId)
    bookId = 1;

    let editPage = () => {
        bookId = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
        console.log("Pre Edit ID:",bookId)
    }

    let data = bookDef
    let deleteBook = async () => {
        let bookId = window.location.href.substring(window.location.href.lastIndexOf('/') + 1);
        console.log("Deleting ID:",bookId)
        await fetch(`http://localhost:8000/api/books/${bookId}`, {
            method: 'DELETE',
            header: {
                'Content-Type': 'application/json',
            }
        })
        window.location.reload();
    }

    let addBook = async () => {
        let response = await fetch(`http://localhost:8000/api/books`, {
            method: 'POST',
            header: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(bookDef)
        })
        data = await response.json()
        console.log("!!! data:",data.id)
        window.location.reload();
    }

    return(
        <ul className='UL-List'>
            <NavLink to={`/edit/${bookId}`} onClick={editPage} className="HeadItem" id="HeadItem-R">
            Edit
            </NavLink>
            <NavLink to='/' onClick={deleteBook} className="HeadItem" id="HeadItem-Del">
            Delete
            </NavLink>
            <NavLink to={'/'} onClick={addBook} className="HeadItem">
            Add
            </NavLink>
            <NavLink to='/' className="HeadItem" id="HeadItem-L">
            Home
            </NavLink>
        </ul>
        
    )
}

export default Header;
