import React, {useEffect, useState} from 'react'
import { Link, useParams } from 'react-router-dom'
// import books from '../assets/data'

const BookPage = () => {
    let params = useParams();
    let bookId = params.id;
    // let book = books.find(book => book.id == params.id)
    let [book, setBook] = useState([])

    useEffect(() => {
        getBook()
    }, [bookId])

    let getBook = async () => {
        let response = await fetch(`http://localhost:8000/api/books/${bookId}`)
        let data = await response.json()
        setBook(data)
    }
    
    return (
        <div className="Box-Text">
            <h3>{book.title} &nbsp;&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp; {book.id}</h3>
            <h7>{book.isbn}</h7>
            <h5>{book.author.firstname} {book.author.lastname}</h5>

            <textarea value={book?.comment}>
            
            </textarea>
        </div>
    )
}

export default BookPage
